var data = msg.payload;
var filtered = {
	shieldPower: data.forShields,
	maxShieldPower: data.payload,
	redAlert: data.redAlert,
	shieldState: data.shieldState,
	docking: data.docking,
	energy: data.energy
};

return {payload: filtered};

var data = msg.payload;
var curDam = 0;
var shipDamage = 0;

console.log("Subsystem count " + data.nodes.length);

for(var i = 0; i < data.nodes.length; i++ ){
	curDam += data.nodes[i].damage;
}

console.log("CurDam " + curDam );

if(curDam > 0){
	shipDamage = curDam/15;
	if(shipDamage > 1){
		shipDamage = 1;
	}
}else{
	shipDamage = 0;
}

console.log("Current ship damage:" + shipDamage );

return {payload: {shipDamage: shipDamage}};

context.state = context.state || {}; 
var data = msg.payload;
for (var key in data) {
    if(data[key] != undefined && data[key] != null){
    	context.state[key] = data[key];
    }

}
return {payload: context.state};

var data = msg.payload;
msg.topic = "redAlert";

if(data.redAlert === 1){
	msg.payload = "on";
}else{
	msg.payload = "off";
}

if(data.redAlert === 1){
    return [null,msg];
}else{
	return [{payload: data},msg];
}

var data = msg.payload;
msg.topic = "shield";

if(data.shieldState === 1){
	msg = {topic:"lights",payload:{}};
    msg.payload.hue = 39072;
	msg.payload.saturation = 0xffff;
	msg.payload.luminance = 0x8000;
	msg.payload.whiteColor = 0;
}else{
	msg = {topic:"lights",payload:{}};
    msg.payload.hue = 37836;
	msg.payload.saturation = 0x4fff;
	msg.payload.luminance = 0x1000;
	msg.payload.whiteColor = 0x0af0;
}

if(data.shieldState === 1){
    return [null,msg];
}else{
	return [{payload: data},msg];
}

var pulseMsg = null;
if(context.redAlertState == undefined){
	context.redAlertState = true;
	context.isOn = false;
}
if(context.inited != true && msg.topic == "redAlert"){
	context.inited = true;
	pulseMsg = {payload: true, topic:"pulse"};
	console.log("pulse inited");
}


if(msg.topic == "redAlert"){
	if(msg.payload == "on"){
		context.isOn = true;
	}else{
		context.isOn = false;
	}
}
var bulbMsg = null;
if(msg.topic == "pulse"){
	if(context.isOn){
		if(context.redAlertState){
			bulbMsg = {topic:"",payload:{}};
		    bulbMsg.payload.hue = 0x0000;
			bulbMsg.payload.saturation = 0xffff;
			bulbMsg.payload.luminance = 0x8000;
			bulbMsg.payload.whiteColor = 0;
		}else{
		    bulbMsg = {topic:"",payload:{}};
		    bulbMsg.payload.hue = 0x0000;
			bulbMsg.payload.saturation = 0xffff;
			bulbMsg.payload.luminance = 0x1000;
			bulbMsg.payload.whiteColor = 0;
		}
	}
	context.redAlertState = !context.redAlertState;
	pulseMsg = {payload: true, topic:"pulse"};
}

return [bulbMsg,pulseMsg];

var data = msg.payload;
msg.topic = "docking";

if(data.docking > 0){
	msg = {topic:"lights",payload:{}};
    msg.payload.hue = 0x2710;
	msg.payload.saturation = 0xffff;
	msg.payload.luminance = 0x8000;
	msg.payload.whiteColor = 0;
}

if(data.docking > 0){
    return [null,msg,{payload:"on"}];
}else{
	return [{payload: data},null,{payload:"off"}];
}
